INSERT INTO store (id, name, details, address, phone, skype, discounts, mail, logo) VALUES (2, 'Мебельмакс', '12 лет мы стараемся для вас! За долгие годы ответственной работы, мы подобрали лучших поставщиков, в лице производителей качественной мебели Республики Беларусь, России и Украины. Выбрав для Вас совершенный ассортимент в золотой середине - цена, качество. Добросовестность и ответственность наших производителей, доставит Вам радость от покупок в нашем мебельном магазине.', 'г. Минск, ул. Долгобродская, д. 17', '8017398-93-33', 'mebelmax', true, '', '51dc9f3b-061b-418d-9d61-233bd430f4f5');
INSERT INTO store (id, name, details, address, phone, skype, discounts, mail, logo) VALUES (3, 'Молодечномебель', 'Возможно, пришло время что-либо поменять? Может, пришла пора сменить мягкую мебель? Купить диван-кровать или кресло? Или полностью обновить мягкую мебель в гостиной, да и во всем доме? Не спешите с ответом, но задумайтесь, а мы тем временем постараемся Вас убедить. Мягкая мебель в Минске сделает Ваш дом ещё более уютным и милым. Посудите сами. Конечно, Ваше гостеприимство и радушие поможет гостям расслабиться и прекрасно почувствовать себя.', 'Минск, пр-т Дзержинского,11, Минск, ул. Калиновского, 55,  Минск, ул.Неманская 3', '80295123546', 'molodechnoMebel', NULL, 'molodechnomebel@pochta.ru', 'f741981f-297f-424c-b75c-bdf89788887f');
INSERT INTO store (id, name, details, address, phone, skype, discounts, mail, logo) VALUES (4, 'Пинскдрев', 'Вот уже более 135 лет ЗАО «Холдинговая компания «Пинскдрев» работает в мебельной индустрии. Компания является лидером на мебельном рынке и является одной из старейших компаний деревообрабатывающей промышленности. За такой долгий период деятельности был накоплен колоссальный опыт в этом нелегком деле, благодаря чему «Пинскдрев» предлагает покупателям качественную, долговечную и надежную мебель из натуральных материалов.', 'г. Минск, пр-кт Машерова 7', '8029515612', 'pinskdrev.by', true, 'pinskdrev@mebel.by', '9c87e690-449a-4ac8-9c14-8bf3c4036d0c');
INSERT INTO store (id, name, details, address, phone, skype, discounts, mail, logo) VALUES (5, 'Неман', 'Фирменный интернет-магазин мебельной фабрики "Мебель-Неман" представляет Вам полный ассортимент продукции и сопутствующих товаров по отличным ценам и всеми гарантиями качества. "Мебель-Неман" - один из крупнейших производителей корпусной мебели для дома. Обладая широкими производственными возможностями, отлаженной схемой работы, а также креативным штатом профессиональных дизайнеров, компания реализует самые смелые проекты, совмещая отменное качество с доступными каждому потребителю ценами.', 'Гродно, ул. Горького 89, корп.2а ', '375(152)486029', 'mebelneman.by', NULL, 'neman@mebel.by', '91ff4e4c-0e09-4153-bc96-3bd3c43ac6fd');
INSERT INTO store (id, name, details, address, phone, skype, discounts, mail, logo) VALUES (6, 'Black Red White', 'Интернет-магазин мебели Black Red White – это магазин мебели и предметов интерьера для Вашего дома. В интернет-магазине мебели Black Red White можно купить мебель производства ИООО «БРВ-Брест», крупнейшего  производителя корпусной мебели, представителя группы компаний Black Red White на территории Беларуси.', 'г. Брест, ул.Московская, 275А (ОЦ Диагональ), офис 501 ', '80291513251', 'brw', true, 'brw@mail.ru', 'e45f8e08-4bfb-4857-8aaa-5bbc940ba7c4');
